window.addEventListener('load',function(){const loadingBox=document.getElementById('loading-box');setTimeout(()=>{loadingBox.style.opacity='0';setTimeout(()=>{loadingBox.style.display='none'},500)},1000);document.getElementById('current-year').textContent=new Date().getFullYear();const cards=document.querySelectorAll('.card');cards.forEach(card=>{card.addEventListener('mouseenter',()=>card.style.transform='translateY(-5px)');card.addEventListener('mouseleave',()=>card.style.transform='translateY(0)')})});