window.addEventListener('load',function(){function updateTimeProgress(){const now=new Date();const startOfDay=new Date(now);startOfDay.setHours(0,0,0,0);const dayProgress=(now-startOfDay)/(24*60*60*1000)*100;document.getElementById('day-progress').textContent=`${dayProgress.toFixed(1)}%`;document.getElementById('day-progress-bar').style.width=`${dayProgress}%`;const startOfWeek=new Date(now);startOfWeek.setDate(startOfWeek.getDate()-startOfWeek.getDay());startOfWeek.setHours(0,0,0,0);const weekProgress=(now-startOfWeek)/(7*24*60*60*1000)*100;document.getElementById('week-progress').textContent=`${weekProgress.toFixed(1)}%`;document.getElementById('week-progress-bar').style.width=`${weekProgress}%`;const startOfMonth=new Date(now.getFullYear(),now.getMonth(),1);const daysInMonth=new Date(now.getFullYear(),now.getMonth()+1,0).getDate();const monthProgress=(now.getDate()-1+now.getHours()/ 24) /daysInMonth*100;document.getElementById('month-progress').textContent=`${monthProgress.toFixed(1)}%`;document.getElementById('month-progress-bar').style.width=`${monthProgress}%`;const startOfYear=new Date(now.getFullYear(),0,1);const isLeapYear=(now.getFullYear()%4===0&&now.getFullYear()%100!==0)||now.getFullYear()%400===0;const daysInYear=isLeapYear?366:365;const yearProgress=((now-startOfYear)/(daysInYear*24*60*60*1000))*100;document.getElementById('year-progress').textContent=`${yearProgress.toFixed(1)}%`;document.getElementById('year-progress-bar').style.width=`${yearProgress}%`}updateTimeProgress();setInterval(updateTimeProgress,60000);});