document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("quote-text"),t=document.getElementById("quote"),n="https://www.hhlqilongzhu.cn/api/yiyan.php";async function o(){try{e.textContent="加载中...";const t=await fetch(n+"?t="+Date.now());if(!t.ok)throw new Error("请求失败");const o=await t.text();e.textContent=o.trim()||"获取语录失败。"}catch(t){e.textContent="加载失败，请点击重试。",console.error("一言加载错误:",t)}}o(),t.addEventListener("click",o)});
